INSERT INTO "cp" VALUES(262,2,'M920','北山坑',236092.158,2653353.954,323.294,'由草屯沿台 14 線往埔里方向行駛至指標 44K+500 處,右轉行下坡路至斗山吊橋前停車,即可見點位於右側空地上。');
INSERT INTO "cp" VALUES(263,2,'M924','西羊稠',167564.508,2615593.997,22.476,'行台 17 線由口湖往台西方向,行至里程 95K+600 處,右轉接 160 線,行 700公尺至飛沙國小,由飛沙國小前續往四湖方向,行約 1.5 公里停車,可見點位於農地重劃紀念碑旁。※點位西側平台約 2 公尺處,有一《台灣省政府地政處四等控制點》。');
INSERT INTO "cp" VALUES(264,2,'M926','王功',181380.593,2651627.245,24.591,'由麥寮沿台 17 線往鹿港方向行至指標 51K+300 處,左轉行 148 線續行 600 公尺遇 T 字路口,往右行 200 公尺再往左往王功漁港方向行 1.6 公里可見點位於堤防上。');
INSERT INTO "cp" VALUES(265,2,'M927','廟前',245200.612,2646882.294,658.866,'由新城橋沿台 21 線往埔里方向,行 250 公尺右轉投 65 線,行約 5.9 公里見電力桿鹽菜幹 168 停車可見點位。');
INSERT INTO "cp" VALUES(266,2,'M928','蔦松',171476.143,2602434.995,22.588,'由 166 縣道崩山分校往六腳方向行 150 公尺左轉,行 1.2 公里叉路右轉行 1.75公里見景陽宮牌樓停車,點於牌樓前 25 公尺處水溝旁。');
INSERT INTO "cp" VALUES(267,2,'M929','沙鹿交流道',208227.682,2682745.332,152.346,'由國道 3 高速公路龍井往清水方向行駛,下沙鹿交流道往沙鹿方向接台 10 線後迴轉往大雅方向,點位於南下匝道口行人穿越道旁。');
INSERT INTO "cp" VALUES(268,2,'N001','七星山',305788.265,2784799.125,1139.563,'由陽明山國家公園遊客中心行台 2 甲往金山方向行 4.4 公里至岔路,右轉上行至小油坑收費停車場停車,行至停車場中間左側可見步道往七星山指標,行步道經過一段噴氣孔上行步道約 1.5 公里,可見點位。');
INSERT INTO "cp" VALUES(269,2,'N002','小八里分',298158.145,2781843.517,253.021,'由政戰學校左側大排水溝邊進入左轉稻香路,繞山直往山脊線,見路標:「大華山莊」及「停車檢查」及吳仔厝標誌停車,步行左轉見鐵塔後再行約 100 公尺,走過紅土空地即到達點位,步行時間約 5 分鐘。註:山背只有二座鐵塔,在關渡平原上可望見。');
INSERT INTO "cp" VALUES(270,2,'N003','大坪山',314139.109,2768490.583,409.543,'由 106 道路(台北往石碇)至 109 道路左轉(南深公路)行至 109 號道路 5k+800,一岔路(大坪街)(有往大坪山標示牌)右轉途經風格山莊,再往前行至叉路,由有大坪山標示牌小路前行至下一路口,往右行至廢棄之養雞場下車,由小木橋步行往上走約 30 分鐘即可到達點位。(註:沿路均有登山布條)');
INSERT INTO "cp" VALUES(271,2,'N004','燦光山',337310.979,2776357.885,759.33,'由瑞芳沿 102 線道往雙溪方向行經九份,前行至指標 18K+850 處見一水泥路,左轉前行 2.95 公里停車,可見右側登山小徑,由此登山 40 分 鐘即可達點位。');
INSERT INTO "cp" VALUES(272,2,'N011','大古山',279402.897,2777164.02,170.511,'由台 15 線 24K+350 處沿桃 1 線往山腳方向出發,行 1.7 公里續接桃 3 線續行2.7 公里,左轉行 300 公尺再左轉往福德祠方向行 1.4 公里停車,沿墓園旁小徑步行 2 分鐘即可達點位。');
INSERT INTO "cp" VALUES(273,2,'N012','龜崙山',288781.004,2765366.734,424.722,'由福源國小大同路直行 1.1km,左轉往大棟山方向,沿路有大凍山土雞城及五路財神廟之指示牌,依指標上山至北區水資源局基地台停車後即可到達點位。(入山口座標:288745.2764760.)');
INSERT INTO "cp" VALUES(274,2,'N020','下員坑',301593.614,2797467.504,48.029,'由淡水沿台 2 線往石門方向行駛至 22K+900 處,即可看見白沙灣旅遊中心之標示牌,左轉行 1 公里下車回走 20 公尺左側有一小徑,沿小徑至最高點即可達點位。※點位被石板蓋住,上有紅漆。');
INSERT INTO "cp" VALUES(275,2,'N022','李棟山',280724.982,2731770.468,1935.874,'由尖石鄉公所沿 120 線行經尖石大橋右接竹 60 線 20.9 公里左轉再行 4.7 公里可見一叉路,左行由登山口循登山步道行 70 分鐘至山頂可見李棟山古堡,點位即在古堡內。李棟山莊登山口坐標:281327.2731433');
INSERT INTO "cp" VALUES(276,2,'N023','大分林山',255061.667,2730384.248,355.504,'沿台 3 線峨嵋往橫山行至北埔(81K+500)、右轉 500 公尺見郵局再沿竹 37 線行2.1 公里至老頭擺客家餐廳、途中經大分林橋、停車於老頭擺客家餐廳、沿其後方登山步道上山 30 分鐘即可到達點位。');
INSERT INTO "cp" VALUES(277,2,'N028','牛埔山',244329.088,2742037.449,118.636,'由新竹沿台 1 線往頭份方向行至指標 78K+400 處,左轉穿越鐵路涵洞後續行至十字路口,左轉續行經台禹科技公司至盡頭停車,沿前方登山小徑上至頂點,即可達點位。');
INSERT INTO "cp" VALUES(278,2,'N032','觀霧',261551.053,2711124.237,2130.202,'由北二高,下竹林交流道,行 120 縣道往竹東方向,行約 2.5 公里後,右轉 123 縣道經竹林大橋後(第一個紅綠燈),左轉進入 122 縣道至 122 線到終點,接大鹿林道行至雲山派出所經入山管制哨(以公文或入山證登記入山),車行至觀霧管理站下車,點位於觀霧管理站左前斜對面小山丘上,步行時間約 5 分鐘,即達點位。');
INSERT INTO "cp" VALUES(279,2,'N035','三貂角',350870.05,2766773.63,124.515,'三貂角位於東北角卯澳與萊萊之間,車行至台 2 線公路 108K+100 公尺處見往三貂角燈塔告示牌,右轉往山上柏油路(行約 850 公尺),見一叉路往 12 公墓牌子左轉柏油路,行約 150 公尺(再左轉續行 70 公尺泥土路為路之盡頭),沿右側墓地旁小徑步行約 6 分鐘(沿路茫草擋道,但路徑明顯)可達點位。');
INSERT INTO "cp" VALUES(280,2,'N039','米曲瑤山',325244.868,2744374.213,245.562,'由台 9 線 78K 礁溪往宜蘭方向前行,至 78k+650 處右轉 192 號縣道,經新生國小至台旭纖維股份有限公司,再過去之民宅大坡路 206 號右轉,續行大坡路、漳福路經台灣化學公司至漳福廟,可見一山頭沿正前方柏油路上山行約 500 公尺有一叉路,直行上山再行 2.3公里至柏油路終點,續行土路 200 公尺至最高點(再行即下坡)處下車,點位於左側山頂上。');
INSERT INTO "cp" VALUES(281,2,'N040','三星山',307149.406,2711536.369,2375.515,'由台 7 甲 3k+700 處沿宜 53 線往太平山出發經土場檢查哨及售票站,再過多望橋續行至 24.25K 處見一大型停車場,往左行翠峰林道至 5K+800 處停車,登山口於左側沿登山布條登頂,登頂可見一瞭望台,點位於瞭望台北方 8 公尺處。');
INSERT INTO "cp" VALUES(282,2,'N041','蘭坎山',327070.482,2714774.993,1497.293,'由台 9 線羅東往蘇澳方向行至 95K+800 處,左轉行宜 35 線約 2.7 公里遇管制站,再行安平坑林道 10.8 公里,左轉行土路 2.1 公里遇五叉路,取最右側行 1.6 公里途經洗石場,左轉土路行 1.5 公里可達土路最高處停車,登山口於左側,循小徑陡坡上行(路徑非常明顯),直行過兩三個山頭,直遇芒草堆可達點位。');
INSERT INTO "cp" VALUES(283,2,'N043','溪子口山',305385.826,2765062.269,163.281,'由文山區景興國中前景興路出發往新店方向行 200 公尺處,遇景華街左轉,行 400公尺處,見右側道路為 150 巷,右轉行 50 公尺,見左側有一景興國小,即停車,步行沿景興國小旁小徑行 150 公尺,即到仙跡岩登山入口,沿階梯山徑行 380 公尺至山頂最高點,點位即位於階梯旁.(步行約 25 分鐘)');
INSERT INTO "cp" VALUES(284,2,'N050','龜山島',346334.359,2748564.898,417.773,'由烏石港、梗枋、大里三處碼頭都可乘船至龜山島,由龜山島碼頭步行經海巡署安檢所、普陀巖、遊客服務中心,可見健行階梯,由階梯步行而上約 40 分鐘可達點位。※龜山島每年 3 月 1 日~10 月 1 日為開放時間。');
INSERT INTO "cp" VALUES(285,2,'N052','蘇澳港',337893.079,2721076.595,25.173,'由頭城沿台 2 線往蘇澳方向行至指標 174K+700 處,左轉往蘇澳港方向至港警局下車辦理通行證,進入檢查哨行 500 公尺,左轉行至堤防可見礁石公園,點位於公園內大石頭上。');
INSERT INTO "cp" VALUES(286,2,'N053','南山',328912.128,2699239.814,560.301,'由太魯閣沿台 9 線往蘇澳方向行至指標 144K+150 處,左轉農宜南 12 道路上行1.75 公里遇叉路右行 1.3 公里,可見一土路(4WD 可行)右轉上行 400 公尺,可見點位於路之右側 5 公尺處。');
INSERT INTO "cp" VALUES(287,2,'N054','林望眼山',299786.095,2741631.725,765.459,'自新店往烏來方向走台 9 甲公路,至 13.3 公里處,右轉經烏來觀光大橋往烏來鄉褔山村方向前進沿北 107-1 縣道,接北 107 縣道至褔山村,可見北 107 縣道終點之標示牌,右轉沿 006-1 道路至 2K 處再右轉至道路盡頭前 500 公尺停車,從左側小徑上山,步行 40 分鐘,可達點位。');
INSERT INTO "cp" VALUES(288,2,'N068','槓子',328206.189,2781853.803,183.1,'由基隆地方法院(東信路 176 號)東行,左轉行東光路,過富貴新村 150m,左轉直行約 900m,可看到小徑上山約 3 分鐘即可達。');
INSERT INTO "cp" VALUES(289,2,'N085','枕頭山',283734.509,2744970.11,652.024,'由大溪往復興方向沿台 7 線公路直行,途中經過圓山一號橋,行至葉記美食館前右轉行 1.15 公里後可見左側有一叉路,再由叉路直行 120 公尺到達登山口,沿登山步道一路爬坡約 45 分鐘即到達點位。');
INSERT INTO "cp" VALUES(290,2,'N090','天送埤',312651.746,2728170.119,207.995,'由三星市區至天送埤台 7 丙正 10K 處,左側往山上行,順水管行至山嶺右轉,順嶺線前進,可到達點位。※需帶砍刀');
