INSERT INTO "cp" VALUES(634,3,'N550','柑腳山',331381.478,2764824.919,635.851,'由雙溪往十分寮方向沿台 2 丙行至內柑腳村,左轉祥柑大橋行至崩山坑巷叉路右轉行至威惠廟,由右邊產業道路行約 8 公里北 42 線 20K+800 即登山口,沿步條上行約 60分鐘即達點位。※由威惠廟至登山口路上約 4~5 座小橋沿主要道路北 42 線');
