INSERT INTO "cp" VALUES(686,3,'N949','利澤簡',334102.694,2729546.021,35.566,'由蘇澳沿台 2 線往頭城方向行,右轉宜 22 號道路行 280 公尺可見仙水寺牌樓,右轉行 400 公尺見電桿(33L4L5)停車,沿花生田旁土路至登山口,上行 3 分鐘即可到達點位。');
INSERT INTO "cp" VALUES(687,3,'N951','塗潭山',301707.304,2759133.724,528.848,'由台北過碧潭大橋後直行安康路,約 300 公尺後,左轉華城路約 5.4 公里,往大台北華城禾豐特區,至禾豐特區須向守衛換鑰匙。沿禾豐三路、禾豐七路、禾豐九路至 16號建物下車,順路邊鐵絲網上山約 10 分鐘到達點位。');
INSERT INTO "cp" VALUES(688,3,'N958','草子崎',282877.733,2776007.687,248.882,'由林口 106 線 0k+000 起點往林口交流道方向,至 3k+100 見台灣山豬城看板,右轉北 120 線經過美麗華球場行約 2.3 公里,到靈濟宮牌樓前往雙巷福德宮方向再行 400 公尺,左轉土路 100 公尺至墓園空地停車,循航跡至點位,點位於葉仁官佳城後方。');
INSERT INTO "cp" VALUES(689,3,'N971','八角子寮',320434.969,2728666.671,178.793,'由三星沿台 7 丙往羅東方向行至 18K+750 處,見右側有一玉尊宮牌樓,右轉宜 46線,往玉尊宮方向行約 1.3 公里,見一土路右轉行 120 公尺,左轉續行 60 尺至土路盡頭停車,(土路泥濘,請小心駕駛). 點位位於左側山頂上,沿途均有布條。');
INSERT INTO "cp" VALUES(690,3,'N982','西子頭',296130.492,2778419.814,93.705,'由五股交流道下往沿 107 甲線往八里方向行駛接 107 線至成德陸橋,左轉行 103線約 300 公尺可見獅子頭景觀公園,左轉沿路續行 1.1 公里遇叉路,遇民宅 66-1 號,右轉小徑約 100 公尺至盡頭停車,沿墓園旁小徑步行遇第一公墓石柱,左轉行至沈氏之墓與林氏之墓之間即可達點位。');
INSERT INTO "cp" VALUES(691,3,'N988','大竹林山',335710.449,2771059.314,324.655,'由牡丹沿 102 線往貢寮方向行至 30K+800 處,右轉行經中正隧道過平林農場約行至台 2 丙 18K+200 處,右轉往竹寮坑方向行約 1.4 公里,右轉續行經民宅(竹寮坑 10 號)再左轉往天元福生宮方向行 1.2 公里至一空地停車,沿登山口行山脊至山頂,即可達點位。');
INSERT INTO "cp" VALUES(692,3,'N989','白匏子湖',318494.248,2774780.314,117.435,'由汐止交流道左轉台 5 線往基隆方向行約 3.6 公里到達汐止鎮邊界之長春貨櫃場,遇一新闢道路左轉新闢道路行約 200 公尺至長春空櫃區入口,進入大門守衛室即右轉直行至盡頭(空地),即見登山布條,停車步行穿越小竹林,遇台電維修道路沿維修道行走至點位約 15 分鐘。');
INSERT INTO "cp" VALUES(693,3,'PKGA','北港追蹤站副點 A',179154.53,2608856.763,27.737,'由台 19 線 65K+500 處北辰派出所前沿新南路往北港農工方向行 700 公尺過太平路停車可見點於暗溝邊。');
