INSERT INTO "cp" VALUES(5237,6,'W901','梁山',180258.186,2698664.687,73.794,'由金城往水頭,前往至古岡湖後,左轉南行,至第二條叉路口,右轉即見一涼亭,大石上刻有" 漢影雲根"四大字,由石旁小徑彎曲上至山頂,新設點距原點5.3公尺處。');
